class Solution(object):
    def firstMissingPositive(self, nums):
        """
        :type nums: List[int]
        :rtype: int
        """

        # 1. mark all negative numbers and all numbers larger than n as 0
        # 2. iterate through the array and mark the numbers as negative
        # 3. iterate through the array again and return the first number that is not negative
        # 4. if all numbers are negative, return n + 1

        n = len(nums)
        for i in range(n):
            if nums[i] <= 0:
                nums[i] = n + 1

        for i in range(n):
            num = abs(nums[i])
            if num <= n:
                nums[num - 1] = -abs(nums[num - 1])

        for i in range(n):
            if nums[i] > 0:
                return i + 1

        return n + 1

