class Solution(object):
    def twoSum(self, numbers, target):
        """
        :type numbers: List[int]
        :type target: int
        :rtype: List[int]
        """

        # create a dictionary
        d = {}

        # iterate through the list
        for i, num in enumerate(numbers):

            # if the difference between the target and the current number is in the dictionary, return the indices
            if target - num in d:
                return [d[target - num] + 1, i + 1]

            # otherwise, add the number to the dictionary with the index as the value
            d[num] = i

