class Solution(object):
    def addOperators(self, num, target):
        """
        :type num: str
        :type target: int
        :rtype: List[str]
        """

        def backtrack(i, curr, prev, res, op):
            if i == len(num):
                if curr == target:
                    res.append(op)
                return
            if i == 0:
                backtrack(i+1, curr, prev, res, op + num[i])
            else:
                backtrack(i+1, curr + int(num[i]), int(num[i]), res, op + "+" + num[i])
                backtrack(i+1, curr - int(num[i]), -int(num[i]), res, op + "-" + num[i])
                if op[-1] != "0":
                    backtrack(i+1, curr - prev + int(num[i]) * prev, int(num[i]) * prev, res, op + "*" + num[i])

        res = []
        backtrack(0, 0, 0, res, "")
        return res

