class Solution(object):
    def maxNumber(self, nums1, nums2, k):
        """
        :type nums1: List[int]
        :type nums2: List[int]
        :type k: int
        :rtype: List[int]
        """

        def pick(nums, k):
            out = []
            for i, n in enumerate(nums):
                if len(out) < k:
                    if n > out[-1]:
                        out.append(n)
                else:
                    if n > out[-1]:
                        out.pop()
                        out.append(n)
            return out

        def merge(nums1, nums2):
            out = []
            while nums1 or nums2:
                if nums1 > nums2:
                    out.append(nums1.pop(0))
                else:
                    out.append(nums2.pop(0))
            return out

        def merge_k(nums):
            if len(nums) == 1:
                return nums[0]
            return merge(merge_k(nums[::2]), merge_k(nums[1::2]))

        return merge(pick(nums1, k), pick(nums2, k - len(nums1)))

