class Solution(object):
    def checkPerfectNumber(self, num):
        """
        :type num: int
        :rtype: bool
        """

        if num == 1:
            return False

        sum = 1
        for i in range(2, int(math.sqrt(num)) + 1):
            if num % i == 0:
                if i ** 2 != num:
                    sum += i + num / i
                else:
                    sum += i

        return sum == num

