class Solution(object):
    def smallestDistancePair(self, nums, k):
        """
        :type nums: List[int]
        :type k: int
        :rtype: int
        """

        nums.sort()
        n = len(nums)
        l, r = 0, nums[-1] - nums[0]
        while l < r:
            m = (l + r) // 2
            cnt = 0
            j = 0
            for i in range(n):
                while j < n and nums[j] - nums[i] <= m:
                    j += 1
                cnt += j - i - 1
            if cnt >= k:
                r = m
            else:
                l = m + 1
        return l

