class Solution(object):
    def toHex(self, num):
        """
        :type num: int
        :rtype: str
        """

        if num == 0:
            return '0'

        hex_dict = {10: 'a', 11: 'b', 12: 'c', 13: 'd', 14: 'e', 15: 'f'}
        hex_list = []
        if num < 0:
            num = num + 2**32
        while num > 0:
            hex_list.append(num % 16)
            num = num // 16

        hex_list.reverse()
        hex_str = ''
        for i in hex_list:
            if i < 10:
                hex_str += str(i)
            else:
                hex_str += hex_dict[i]
        return hex_str

