class Solution(object):
    def firstUniqChar(self, s):
        """
        :type s: str
        :rtype: int
        """

        # create a dictionary with the frequency of each character
        # iterate through the string and return the first character with frequency 1
        # return -1 if no character with frequency 1

        freq = {}

        for char in s:
            if char not in freq:
                freq[char] = 1
            else:
                freq[char] += 1

        for i, char in enumerate(s):
            if freq[char] == 1:
                return i

        return -1

