class Solution(object):
    def minWindow(self, s, t):
        """
        :type s: str
        :type t: str
        :rtype: str
        """

        if len(t) > len(s):
            return ""

        if len(t) == 0:
            return ""

        if len(t) == 1:
            return s

        t_dict = {}
        for c in t:
            if c not in t_dict:
                t_dict[c] = 1
            else:
                t_dict[c] += 1

        s_dict = {}
        for c in s:
            if c not in s_dict:
                s_dict[c] = 1
            else:
                s_dict[c] += 1

        if t_dict == s_dict:
            return s

        left = 0
        right = 0
        min_len = len(s) + 1
        min_left = 0
        min_right = 0

        while right < len(s):
            if s[right] in t_dict:
                if s_dict[s[right]] > 0:
                    t_dict[s[right]] -= 1
                else:
                    s_dict[s[right]] -= 1
            right += 1

            while len(t_dict) == len(s_dict):
                if right - left < min_len:
                    min_len = right - left
                    min_left = left
                    min_right = right
                if s[left] in t_dict:
                    if s_dict[s[left]] > 0:
