                for _ in range(len(q)):
                    x, y = q.pop(0)
                    if (x, y) == end:
                        return steps
                    for dx, dy in [(0, 1), (1, 0), (0, -1), (-1, 0)]:
                        nx, ny = x + dx, y + dy
                        if 0 <= nx < m and 0 <= ny < n and forest[nx][ny] > 0 and (nx, ny) not in visited:
