class Solution(object):
    def insert(self, intervals, newInterval):
        """
        :type intervals: List[List[int]]
        :type newInterval: List[int]
        :rtype: List[List[int]]
        """

        # if the new interval is empty, return the original list
        if not newInterval:
            return intervals

        # if the original list is empty, return the new interval
        if not intervals:
            return [newInterval]

        # if the new interval is smaller than the first element of the original list, insert it at the beginning
        if newInterval[0] < intervals[0][0]:
            return [newInterval] + intervals

        # if the new interval is larger than the last element of the original list, insert it at the end
        if newInterval[1] > intervals[-1][1]:
            return intervals + [newInterval]

        # if the new interval overlaps with the first element of the original list, merge the new interval with the first element
        if newInterval[0] <= intervals[0][0] and newInterval[1] >= intervals[0][1]:
            return [newInterval] + intervals[1:]

        # if the new interval overlaps with the last element of the original list, merge the new interval with the last element
        if newInterval[0] >= intervals[-1][0] and newInterval[1] <= intervals[-1][1]:
            return intervals[:-1] + [newInterval]

        # if the new interval overlaps with the middle elements of the original list, merge the new interval with the middle elements
        for i in range(len(intervals)):
            if newInterval[0] >= intervals[i][0] and newInterval[1] <= intervals[i][1]:
                return intervals[:i] + [newInterval] + intervals[i+1:]

        # if the new interval does not overlap with any element of the original list, insert it at the appropriate position
        for i in range(len(intervals)):
            if newInterval[0] > intervals[i][1]:
                continue
            elif newInterval[1] < intervals[i][0]:
                return intervals[:i] + [newInterval] + intervals[i:]
