class Solution(object):
    def search(self, nums, target):
        """
        :type nums: List[int]
        :type target: int
        :rtype: int
        """

        # find the pivot
        pivot = self.find_pivot(nums)

        # if the pivot is not found, then the array is not rotated
        if pivot == -1:
            return self.binary_search(nums, target)

        # if the target is less than the first element, then it is in the second half
        if nums[pivot] == target:
            return pivot

        # if the target is less than the first element, then it is in the second half
        if nums[0] <= target:
            return self.binary_search(nums[:pivot+1], target)

        # if the target is less than the first element, then it is in the second half
        return self.binary_search(nums[pivot+1:], target)

    def find_pivot(self, nums):
        left, right = 0, len(nums) - 1

        while left < right:
            mid = left + (right - left) // 2

            if nums[mid] > nums[mid + 1]:
                return mid

            if nums[mid] < nums[mid - 1]:
                return mid - 1

            if nums[mid] > nums[left]:
                left = mid + 1
            else:
                right = mid - 1

        return -1

    def binary_search(self, nums, target):
        left, right = 0, len(nums) - 1

        while left <= right:
            mid = left + (right - left) // 2

            if nums[mid] == target:
                return mid

            if nums[mid] < target:
                left = mid + 1
            else:
                right = mid - 1

        return -1

