class Solution(object):
    def convert(self, s, numRows):
        """
        :type s: str
        :type numRows: int
        :rtype: str
        """

        if numRows == 1:
            return s

        res = [""] * numRows
        currRow = 0
        goingDown = False

        for c in s:
            res[currRow] += c
            if currRow == 0 or currRow == numRows - 1:
                goingDown = not goingDown
            currRow += 1 if goingDown else -1

        return "".join(res)

