class Solution(object):
    def solveSudoku(self, board):
        """
        :type board: List[List[str]]
        :rtype: None Do not return anything, modify board in-place instead.
        """

        def find_empty_location(board,l):
            for row in range(9):
                for col in range(9):
                    if board[row][col] == '.':
                        l[0] = row
                        l[1] = col
                        return True
            return False

        def used_in_row(board,row,num):
            for col in range(9):
                if board[row][col] == num:
                    return True
            return False

        def used_in_col(board,col,num):
            for row in range(9):
                if board[row][col] == num:
                    return True
            return False

        def used_in_box(board,row,col,num):
            for i in range(3):
                for j in range(3):
                    if board[i+row][j+col] == num:
                        return True
            return False

        def is_valid(board,row,col,num):
            return not used_in_row(board,row,num) and not used_in_col(board,col,num) and not used_in_box(board,row-row%3,col-col%3,num)

        def solve_sudoku(board):
            l = [0,0]
            if not find_empty_location(board,l):
                return True
            row = l[0]
            col = l[1]
            for num in range(1,10):
                if is_valid(board,row,col,str(num)):
                    board[row][col] = str(num)
                    if solve_sudoku(board):
                        return True
                    board[row][col] = '.'
            return False

        solve_sudoku(board)

