# Definition for a binary tree node.
# class TreeNode(object):
#     def __init__(self, val=0, left=None, right=None):
#         self.val = val
#         self.left = left
#         self.right = right
class Solution(object):
    def recoverTree(self, root):
        """
        :type root: TreeNode
        :rtype: None Do not return anything, modify root in-place instead.
        """

        # find the first and second node
        first = second = None
        prev = None
        while root:
            if root.left:
                # find the inorder predecessor
                pred = root.left
                while pred.right and pred.right != root:
                    pred = pred.right
                if pred.right == root:
                    pred.right = None
                    if prev and prev.val > root.val:
                        second = root
                        if not first:
                            first = prev
                    prev = root
                    root = root.right
                else:
                    pred.right = root
                    root = root.left
            else:
                if prev and prev.val > root.val:
                    second = root
                    if not first:
                        first = prev
                prev = root
                root = root.right
        first.val, second.val = second.val, first.val

