            tmp = board[i][j]
            if tmp not in node.children:
                return False
            board[i][j] = '#'
            res = dfs(i + 1, j, node.children[tmp]) or dfs(i - 1, j, node.children[tmp]) or dfs(i, j + 1, node.children[tmp]) or dfs(i, j - 1, node.children[tmp])
            board[i][j] = tmp
            return res

        trie = {}
        for word in words:
            node = trie
            for c in word:
                if c not in node:
                    node[c] = {}
                node = node[c]
            node['isWord'] = True
            node['word'] = word

        res = []
        for i in range(len(board)):
            for j in range(len(board[0])):
                if board[i][j] in trie:
                    dfs(i, j, trie[board[i][j]])
        return res

