class Solution(object):
    def wordPattern(self, pattern, s):
        """
        :type pattern: str
        :type s: str
        :rtype: bool
        """

        # split the string into words
        words = s.split()

        # check if the length of pattern and words are equal
        if len(pattern) != len(words):
            return False

        # create a dictionary to store the mapping
        mapping = {}

        # iterate over the pattern and words
        for i, j in zip(pattern, words):
            # if the pattern is not in the mapping, add it
            if i not in mapping:
                if j in mapping.values():
                    return False
                mapping[i] = j
            # if the word is already in the mapping, check if it matches the pattern
            elif mapping[i] != j:
                return False

        # if we reach here, all words are mapped correctly
        return True

